$ns.natalchart = function ($planets) {
  var ctx = document.getElementById('canvas').getContext('2d');
  var planetX;
  var planetY;
  var innerX;
  var innerY;
  var middleX;
  var middleY; 
  var outerX;
  var outerY;
  var glyphX;
  var glyphY;
  var signDegree;

  var signImageArray = new Array();
  var planetImageArray = new Array();

  var $planetColor = {
		sun: "#feb400",
		moon: "#b2b2b2",
		mercury: "#abc600",
		venus: "#00ccce",
		mars: "#be0000",
		jupiter: "#0000cb",
		saturn: "#a28868",
		chiron: "#666666",
		uranus: "#bc00ca",
		neptune: "#116f43",
		pluto: "#6b0000"
  };
    
  ctx.save();
  // Clear out the canvas for multiple executions
  ctx.clearRect(0, 0, canvas.width, canvas.height);
  // Move the 0,0 point to the center of the canvas
  ctx.translate(canvas.width/2, canvas.height/2);
  ctx.strokeStyle = "black";
  ctx.fillStyle = "white";
  ctx.lineWidth = 1;

  // Sign Cusps marks
  ctx.save();
  ctx.lineWidth = 0.75;
  for (var i = 0; i < 12; i++) {
    ctx.beginPath();
    ctx.rotate(Math.PI / 6);
    ctx.moveTo(195-60, 0);
    ctx.lineTo(195, 0);
    ctx.stroke();
  }
  ctx.restore();

  // Decan marks
  ctx.save();
  ctx.lineWidth = 0.25;
  for (var i = 0; i < 36; i++) {
    ctx.beginPath();
    ctx.rotate(Math.PI / 18);
    // Outer wheel ticks
    ctx.moveTo(195-10, 0);
    ctx.lineTo(195, 0);
    ctx.stroke();
    // Inner Wheel ticks
    ctx.moveTo(195-60, 0);
    ctx.lineTo(195-60+6, 0);
    ctx.stroke();

  }
  ctx.restore();
  
  // Degree marks
  ctx.save();
  ctx.lineWidth = 0.1;
  for (i = 0; i < 360; i++) {
    if (i % 30 != 0) {
      ctx.beginPath();
      // Outer wheel ticks
      ctx.moveTo(195-5, 0);
      ctx.lineTo(195, 0);
      ctx.stroke();
      // Inner wheel ticks
      ctx.moveTo(195-60, 0);
      ctx.lineTo(195-60+3, 0);
      ctx.stroke();
    }
    ctx.rotate(Math.PI / 180);
  }
  ctx.restore();
  ctx.fillStyle = "black";

  // Draw outside wheel with radius of 195
  ctx.save();
  ctx.beginPath();
  ctx.lineWidth = 1;
  ctx.strokeStyle = '#000000';
  ctx.arc(0, 0, 195, 0, Math.PI * 2, true);
  ctx.stroke();
  ctx.restore();

  // Draw inside wheel with radius of 60 less than outside
  ctx.save();  
  ctx.beginPath();
  ctx.lineWidth = 1;
  ctx.strokeStyle = '#000000';
  ctx.arc(0, 0, 195-60, 0, Math.PI * 2, true);
  ctx.stroke();
  ctx.restore();

  // Draw inside circle with radius of 70 
  ctx.save();  
  ctx.beginPath();
  ctx.lineWidth = 1;
  ctx.strokeStyle = '#000000';
  ctx.arc(0, 0, 70, 0, Math.PI * 2, true);
  ctx.stroke();
  ctx.restore();

  // Debugging Image to help determine placement. Hard to see, and typically use the dot10 instead.
  var dot = new Image();
  dot.src = 'data:image/png;base64,';

  // Debugging Image to help determine placement, which is the upper left-hand corner of the square
  var dot10 = new Image();
  dot10.src = 'data:image/png;base64,';

  // Aries glyph image
  signImageArray[0] = new Image();
  signImageArray[0].src = 'data:image/png;base64,';

  // Taurus glpyh image
  signImageArray[1] = new Image();
  signImageArray[1].src = 'data:image/png;base64,';

  // Gemini glyph image
  signImageArray[2] = new Image();
  signImageArray[2].src = 'data:image/png;base64,';

  // Cancer glyph image
  signImageArray[3] = new Image();
  signImageArray[3].src = 'data:image/png;base64,';

  // Leo glyph image
  signImageArray[4] = new Image();
  signImageArray[4].src = 'data:image/png;base64,';

  // Virgo glyph image
  signImageArray[5] = new Image();
  signImageArray[5].src = 'data:image/png;base64,';

  // Libra glyph image
  signImageArray[6] = new Image();
  signImageArray[6].src = 'data:image/png;base64,';

  // Scorpio glyph image
  signImageArray[7] = new Image();
  signImageArray[7].src = 'data:image/png;base64,';

  // Sagittarius glyph image
  signImageArray[8] = new Image();
  signImageArray[8].src = 'data:image/png;base64,';
  
  // Capricorn glyph image
  signImageArray[9] = new Image();
  signImageArray[9].src = 'data:image/png;base64,';

  // Aquarius glyph image
  signImageArray[10] = new Image();
  signImageArray[10].src = 'data:image/png;base64,';

  // Pisces glyph image
  signImageArray[11] = new Image();
  signImageArray[11].src = 'data:image/png;base64,';
;
  
  // Sun glyph image
  planetImageArray['sun'] = new Image();
  planetImageArray['sun'].src = 'data:image/png;base64,';

  // moon glyph image
  planetImageArray['moon'] = new Image();
  planetImageArray['moon'].src = 'data:image/png;base64,';
  
  // mercury glyph image
  planetImageArray['mercury'] = new Image();
  planetImageArray['mercury'].src =  'data:image/png;base64,';
  
  // venus glyph image
  planetImageArray['venus'] = new Image();
  planetImageArray['venus'].src =  'data:image/png;base64,';
  
  // mars glyph image
  planetImageArray['mars'] = new Image();
  planetImageArray['mars'].src =  'data:image/png;base64,';
  
  // jupiter glyph image
  planetImageArray['jupiter'] = new Image();
  planetImageArray['jupiter'].src =  'data:image/png;base64,';
  
  // saturn glyph image
  planetImageArray['saturn'] = new Image();
  planetImageArray['saturn'].src =  'data:image/png;base64,';
  
  // uranus glyph image
  planetImageArray['uranus'] = new Image();
  planetImageArray['uranus'].src =  'data:image/png;base64,';
  
  // neptune glyph image
  planetImageArray['neptune'] = new Image();
  planetImageArray['neptune'].src =  'data:image/png;base64,';
  
  // pluto glyph image
  planetImageArray['pluto'] = new Image();
  planetImageArray['pluto'].src =  'data:image/png;base64,';
  
  // chiron glyph image
  planetImageArray['chiron'] = new Image();
  planetImageArray['chiron'].src =  'data:image/png;base64,';
  

  // Reset transform to the upper right-hand corner
  ctx.setTransform(1, 0, 0, 1, 0, 0);   

/*
  // Draw Debugging Grid marks every 10 units
  ctx.save();
  ctx.lineWidth = 0.25;
  for (var i = 0; i < canvas.height/10+1; i++) {
    ctx.beginPath();
    ctx.moveTo(10*i, 0);
    ctx.lineTo(10*i, canvas.height);
    ctx.stroke();
    ctx.moveTo(0, 10*i);
    ctx.lineTo(canvas.width, 10*i);
    ctx.stroke();

  }
  ctx.restore();

  // Draw Debugging Grid marks every 50 units
  ctx.save();
  ctx.lineWidth = 0.5;
  for (var i = 0; i < canvas.height/50+1; i++) {
    ctx.beginPath();
    ctx.moveTo(50*i, 0);
    ctx.lineTo(50*i, canvas.height);
    ctx.stroke();
    ctx.moveTo(0, 50*i);
    ctx.lineTo(canvas.width, 50*i);
    ctx.stroke();
  }
  ctx.restore();
*/

  // Draw Planet Degree
  // Go from the outside wheel to the inside circle
  ctx.lineWidth = 0.5;
  for (var key in $planets) {
	  ctx.save();
	  ctx.beginPath();

      // Draw the planet degree line
	  ctx.strokeStyle = $planetColor[key];
	  ctx.fillStyle = $planetColor[key];
      planetX = Math.cos((180-($planets[key]))*Math.PI/180);
      planetY = Math.sin((180-($planets[key]))*Math.PI/180);
	  innerX = 70 * planetX + (canvas.width/2);
	  innerY = 70 * planetY + (canvas.height/2);
	  middleX = 125 * planetX + (canvas.width/2);
	  middleY = 125 * planetY + (canvas.height/2); 
	  outerX = 195 * planetX + (canvas.width/2);
	  outerY = 195 * planetY + (canvas.height/2);
	  ctx.moveTo(innerX, innerY);
      ctx.lineTo(outerX, outerY);
	  ctx.stroke();

      // Draw aspect dot on inner circle
	  ctx.moveTo(innerX, innerY);
      ctx.arc(innerX, innerY, 2, 0, Math.PI * 2, true);
      ctx.fill();
        
      // Place planetary glyph
	  glyphX = 210 * planetX + (canvas.width/2) - 15;
	  glyphY = 210 * planetY + (canvas.height/2) - 15;
	  ctx.drawImage(planetImageArray[key],glyphX,glyphY);	  
	  
	  ctx.font = "14px Arial";
      signDegree = Math.floor($planets[key] % 30);

      ctx.fillText(signDegree, middleX-7, middleY+5);

	  ctx.restore();
  }

   // Draw the Sign Glyphs
	ctx.save();
	for(var i=0; i<12; i++){
	  ctx.save();
	  // Radius * Angle(+15 degrees from cusp (with 180 correction for Aries zero-point)) + Center Point (minus half the width of a glyph)
	  var x = 165 * Math.cos((180-15-(i*30))*Math.PI/180) + (canvas.width/2)-15;
	  var y = 165 * Math.sin((180-15-(i*30))*Math.PI/180) + (canvas.height/2)-15;
	  ctx.drawImage(signImageArray[i],x,y);
	  ctx.restore();
	}
	ctx.restore();

};
